<script type="text/javascript">
    $(document).ready(function() {
        $('.group-faq .btn-toggle-faq').on('click', function (evt) {
            evt.preventDefault();
            let item = $(this).closest('.group-faq');
            item.toggleClass('collapsed');

            let opener = item.find('.opener');
            let closer = item.find('.closer');
            let answer = item.find('.faq-answer');

            console.log(item, $(item).hasClass('collapsed'))
            if ($(item).hasClass('collapsed')) {
                opener.addClass('hidden');
                closer.removeClass('hidden');
                answer.addClass('hidden');
            } else {
                opener.removeClass('hidden');
                closer.addClass('hidden');
                answer.removeClass('hidden');
            }
        });

        $('.main-nav li a').on('click', function (evt) {
            evt.preventDefault();
            let target = $(this).attr('data-target');

            $([document.documentElement, document.body]).animate({
                scrollTop: $("#" + target).offset().top
            }, 1000);
        })

    })
</script>
