<script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.7.1/jquery.min.js"></script>
<script type="text/javascript">
    $(document).ready(function() {
        $('.group-faq .btn-toggle-faq').on('click', function (evt) {
            evt.preventDefault();
            let item = $(this).closest('.group-faq');
            item.toggleClass('collapsed');

            let opener = item.find('.opener');
            let closer = item.find('.closer');
            let answer = item.find('.faq-answer');

            console.log(item, $(item).hasClass('collapsed'))
            if ($(item).hasClass('collapsed')) {
                opener.addClass('hidden');
                closer.removeClass('hidden');
                answer.addClass('hidden');
            } else {
                opener.removeClass('hidden');
                closer.addClass('hidden');
                answer.removeClass('hidden');
            }
        });

        $('.main-nav li a.nav-item').on('click', function (evt) {
            evt.preventDefault();
            let target = $(this).attr('data-target');
            console.log(target, $("#" + target).offset().top, $("#" + target).offset().top - $('header').height()*2)

            $([document.documentElement, document.body]).animate({
                scrollTop: $("#" + target).offset().top - $('header').height() * 2
            }, 1000);
        });

        $('.menu-toggle').on('click', function (evt) {
            evt.preventDefault();
            let menu = $('.main-nav');
            menu.toggleClass('open');

            if (menu.hasClass('open')) {
                menu.removeClass('translate-x-full');
            } else {
                menu.addClass('translate-x-full');
            }
        })

        $('.close-menu').on('click', function (evt) {
            evt.preventDefault();
            let menu = $('.main-nav');
            menu.removeClass('open').addClass('translate-x-full');
        });

    })
</script>
</body>
</html>
